int Enemy_X;
int Enemy_Cnt;	//敵の数をカウント
int angle_flg;

/***********************************************
 * 構造体の宣言
 ***********************************************/
struct ENEMY_2 {
	int x,y;		// 座標 x,y
	int px,py;		// 移動前座標
	bool angle;		// 向いている方向。 左0←	→1右
	int move;	// 画像反転フラグ
	int life;		// 踏まれたかのフラグ
};
struct ENEMY_2 g_Enemy[30];


/***********************************************
 エネミー初期化
 **********************************************/
void EnemyInit(void){
	
	LoadDivGraph("Image/mob.png",20,10,2,32,48,Enemy_Image);
	
	Enemy_flg = 0;
	Enemy_Cnt = 0;
	
	//g_Enemy.move = -ENEMY_MOVE_SPEED;
	angle_flg = 0;
	for (int i = 0; i < MAP_HEIGHT; i++) {
		for (int j = 0; j < MAP_WIDTH; j++) {
			if(E_g_InitStage[i][j] == 68){
			g_Enemy[Enemy_Cnt].x = j*BLOCKSIZE;
			g_Enemy[Enemy_Cnt].y = i*BLOCKSIZE;
			Enemy_Cnt++;
			}
		}
	}
	if(Enemy_Cnt >= 1)Enemy_flg = 1;
	
}


/***********************************************
 エネミー制御
 **********************************************/
void EnemyControll(void){

	if(Enemy_flg == 1){
		// 移動反転処理
		for(int z=0; z<Enemy_Cnt; z++){
			angle_flg = IsBlock(g_Enemy[z].x/BLOCKSIZE,g_Enemy[z].y/BLOCKSIZE);
			if(angle_flg == 1) g_EnemySpeed *= -1;
			//if(g_Enemy.x > SCREEN_WIDTH-BLOCKSIZE/2) g_Enemy.move = -ENEMY_MOVE_SPEED;
			if( abs(g_Enemy[z].x - g_ScrollQuantity) < 700){
				g_Enemy[z].x = g_Enemy[z].x - g_EnemySpeed;
			}

			//歩くアニメーション
			if(g_WorkCount < 30){
				g_WorkCount++;
				g_Enemy[z].angle = 0;
			}else if(g_WorkCount <= 60){
				g_WorkCount++;
				if(g_WorkCount == 60)g_WorkCount = 0;
				g_Enemy[z].angle = 1;
			}
			
		}
		DrawEnemy();
	}


}

